const express = require('express');
const todoRouter = express.Router();
const { v4: uuidv4 } = require("uuid");

const todos = [
  {
    name: "Wash Dishes",
    description: "Wash Dirty Dishes",
    imageUrl:
      "data:image/jpeg;base64,",
    completed: false,
    id: uuidv4(),
  },
  {
    name: "Give Dog A Bath",
    description: "Give Sunny Her Monthly Bath",
    imageUrl:
      "data:image/jpeg;base64,",
    completed: false,
    id: uuidv4(),
  },
  {
    name: "Do Homework",
    description: "Do Programming Homework for V-School",
    imageUrl:
      "data:image/jpeg;base64,",
      completed: false,
      id: uuidv4()
  },
];




// Routes To Get and Create Todos
todoRouter.route('/')
    .get((req, res) => {
        res.send(todos);
    })
    .post((req, res) => {
        const newTodo = req.body;

        if (newTodo.hasOwnProperty("name") && newTodo.hasOwnProperty("description") && newTodo.hasOwnProperty("completed")) {
            // If A Newly Created Todo Meets All The Criteria, Assign The Todo an ID
            // Add The New Todo To The Array of Todos
            // Send Response Back Telling User That The Todo Has Been Created
            newTodo.id = uuidv4();
            todos.push(newTodo);
            res.status(201).send(`You Have Successfully Added ${newTodo.name} to Your Todo List`)
        }
        else {
            res.status(500).send("You Are Missing One or More Required Entries in Your Todo Item");
        }
    })

// Routes To Update and Delete Todos
// As Well As Get a Single Todo Item
todoRouter.route('/:id')
    .get((req, res) => {
        const todoID = req.params.id;

        if (!todoID) {
            res.status(500).send("Please Provide A Valid Todo ID in Order to View a Todo Item");
        }

        const retrievedTodoItem = todos.findIndex((item) => item.id === todoID);

        if (!retrievedTodoItem) {
            res.status(404).send(`Sorry, ID: ${todoID} is Not Associated With Any of The Todo Items`);
        }

        res.send(todos[retrievedTodoItem]);
    })
    .put((req, res) => {
        const todoID = req.params.id;

        if (!todoID) {
            res.status(500).send("Please Provide The Required ID of The Todo Item to Update it");
        }

        const itemToUpdate = todos.findIndex((item) => item.id === todoID);

        if (!itemToUpdate) {
            res.status(404).send(`Sorry, The ID: ${todoID} Is Not Associated With Any Todo Items`)
        }

        const updatedItem = Object.assign(todos[itemToUpdate], req.body);

        res.send(updatedItem);
    })
    .delete((req, res) => {
        const todoID = req.params.id;

        if (!todoID) {
            res.status(500).send("Please Provide The Required ID of The Todo Item That Needs to be Deleted");
        }

        const itemToDelete = todos.findIndex((item) => item.id === todoID);

        if (itemToDelete) {
            res.send(
              "The Todo Item Could Not Be Found. Please Provide A Valid Todo ID In Order to Delete An Item"
            );
        }
        
        todos.splice(itemToDelete, 1);
        res.send("Todo Item Was Deleted Successfully");
    })

    module.exports = todoRouter;